package C2TC;
public class evenoddwithoutarithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 11;	
        if((num & 1) == 0)
        {
        	System.out.println(num +" is even number");
        }
        else
        {
        	System.out.println(num +" is odd number");	
        }
	}

}
